INSERT INTO `bk_projetos` (`id`, `dd_portfolio`, `id_cliente`, `id_parceiro`, `id_tags`, `id_tb`, `id_suporte`, `projeto_pt`, `projeto_en`, `url`, `tx_info_pt`, `tx_info_en`, `tx_cred_pt`, `tx_cred_en`, `publicado_pt`, `publicado_en`, `data`, `layout`, `display`, `banners`) VALUES
(9, 0, 11, 0, '22 24', 319, 1, 'zehbra.com.br', '', 'http://www.zehbra.com.br', '<p>Website do escrit&oacute;rio de arquitetura <a href="http://www.zehbra.com.br" target="_blank">Zehbra Arquitetos</a>.</p>\r\n<p>Este &eacute; um trabalho muito importante, pois foi o primeiro sistema de administra&ccedil;&atilde;o de conte&uacute;do que constru&iacute; para um cliente de verdade. Fiquei bastante satisfeito com o resultado final, pois consegui resolver todas as quest&otilde;es t&eacute;cnicas sem (muita) dor de cabe&ccedil;a e sem nenhuma interfer&ecirc;ncia no visual do site.</p>', '', '<p><b>linguagens<br /></b>PHP 5.0<br />XML<br />javascript<br />ActionScript 2.0<br />HTML+CSS<br /><br /><b>programas</b><br />Flash <br />Dreamweaver <br />Photoshop</p>', '<p><b>languages<br /></b>PHP 5.0<br />XML<br />javascript<br />ActionScript 2.0<br />HTML+CSS<br /><br /><b>softwares</b><br />Flash CS3<br />Dreamweaver CS3<br />Photoshop CS3</p>', 1, NULL, '2009-03-01', '', NULL, '319'),
(10, 0, 8, 0, '21 22 24', 316, 1, 'questafinta.com.br', '', 'http://www.questafinta.com.br', '<p>Website da banda paulistana Questafinta.</p>\r\n<p>Esse &eacute; de longe o website mais legal que j&aacute; produzi. A ideia da vaquinha dan&ccedil;ante j&aacute; era bem antiga, como pode ser visto na <a href="http://www.questafinta.com.br/antigo" target="_blank">vers&atilde;o 1.0</a> (2001) desse trabalho, mas at&eacute; ent&atilde;o n&atilde;o havia tecnologia nem conhecimentos t&eacute;cnicos (meus) suficientes pra concretiz&aacute;-la com esse grau de interatividade.</p>', '', '<p><b>linguagens</b><br />ActionScript 2.0</p>\r\n<p><b>programas</b><br />Adobe Flash</p>', '', 1, NULL, '2008-06-01', '', NULL, '316'),
(11, 0, 8, 0, '21 22', 318, 1, 'questafinta.com.br - v1', '', '', '<p>Website antigo da Banda Questafinta, lan&ccedil;ado em 2001.</p>\r\n<p>Este trabalho est&aacute; aqui mais pelo valor sentimental e hist&oacute;rico do que profissional, pois ele &eacute; antigo e um pouco equivocado tecnicamente, mas foi meu primeiro trabalho grande em webdesign. Al&eacute;m disso, 8 anos depois, lan&ccedil;amos a <a href="http://www.questafinta.com.br" target="_blank">vers&atilde;o 2.0</a> com muito mais recursos de programa&ccedil;&atilde;o e design, e acho curioso ver a evolu&ccedil;&atilde;o. Independente disso, at&eacute; hoje considero ambos igualmente criativos.</p>', '', '<p><b>programas</b><br />Flash</p>', '', 1, NULL, '2001-07-01', '', NULL, '318'),
(12, 0, 50, 0, '20 21', 314, 2, 'Assinatura Santa Edit', '', '', '<p>Assinatura em anima&ccedil;&atilde;o para a produtora de v&iacute;deos Santa Edit.</p>', '', '<p><strong>co-autoria</strong><br />Fabio Issao</p>\r\n<p><strong>programas</strong><br />Adobe Illustrator <br />Adobe After Effects</p>', '', 1, NULL, '2009-09-02', '', NULL, '314'),
(13, 0, 1, 53, '21', 313, 2, 'Vídeo Brasiliana - v2', '', '', '<p>Dire&ccedil;&atilde;o de arte, p&oacute;s-produ&ccedil;&atilde;o e vinhetas de abertura para s&eacute;rie de v&iacute;deos institucionais da <a href="http://www.brasiliana.usp.br/" target="_blank">Biblioteca Brasiliana USP</a>.</p>', '', '<p><strong>dire&ccedil;&atilde;o</strong><br />Luiz Bargmann</p>\r\n<p><strong>programas</strong><br />Adobe Illustrator <br />Adobe After Effecs <br />Adobe Photoshop</p>', '', 1, NULL, '2009-06-01', '', NULL, '313'),
(14, 0, 15, 9, '21', 312, 2, 'Assinatura institucional', '', '', '<p>Assinatura para v&iacute;deos publicit&aacute;rios do portal <a href="http://www.limao.com.br" target="_blank">www.limao.com.br</a>, produzidos pela <a href="http://www.ioiofilmes.com/" target="_blank">Ioi&ocirc; filmes</a>.</p>', '', '<p><strong>produtora</strong><br /><a href="http://www.ioiofilmes.com/" target="_blank">Ioi&ocirc; filmes</a></p>\r\n<p><strong>ilustra&ccedil;&atilde;o</strong><br />Fabio Issao</p>\r\n<p><strong>programas</strong><br />Adobe After Effects <br />Adobe Illustrator</p>\r\n<p>&nbsp;</p>', '', 1, NULL, '2007-07-01', '', NULL, '312'),
(15, 0, 13, 0, '22 24', 311, 1, 'Painel multimídia', '', '', '<p>Multim&iacute;dia interativa para o evento Iguatemi <a href="http://www.filmefashion.com.br/" target="_blank">Filme+Fashion</a> 2007, contendo programa&ccedil;&atilde;o, sinopses e trailers dos filmes.</p>', '', '<p><b>cenografia</b><br />Marcelo Pontes</p>\r\n<p><b>programas</b><br />Adobe Flash</p>\r\n<p><b>linguagens</b><br />ActionScript 2.0</p>', '', 1, NULL, '2007-09-01', '', NULL, '311'),
(16, 0, 14, 0, '22 24', 310, 1, 'chicobela.com', '', 'http://www.chicobela.com', '<p>Website do animador, ilustrador e grande figura Chico Bela.</p>', '', '<p><b>programas</b><br />Adobe Flash</p>\r\n<p><b>linguagens</b><br />ActionScript 2.0</p>', '', 1, NULL, '2007-04-01', '', NULL, '310'),
(17, 0, 17, 44, '22', 309, 1, 'usebens.com.br', '', 'http://www.usebens.com.br', '<p>Website da seguradora que pertence ao <a href="http://www.rodobens.com/" target="_blank">Grupo Rodobens</a>.</p>', '', '<p><b>ag&ecirc;ncia</b><br /><a href="http://www.1mais1design.com.br/" target="_blank">1+1 publicidade&amp;design</a></p>\r\n<p><b>linguagens</b><br />html+css</p>\r\n<p><b>programas</b><br />Adobe Dreamweaver <br />Adobe Photoshop</p>', '', 1, NULL, '2008-07-01', '', NULL, '309'),
