INSERT INTO `TRCOM` (`cel`, `senha`, `email`, `atualizado`, `tx_sobre_pt`, `tx_sobre_en`, `tx_cv_pt`, `tx_cv_en`, `img_link`, `img_lb_pt`, `img_lb_en`, `reel`, `token`, `token_time`) VALUES
('+5511 996 16 2371', '1f5a285dad0e57176f0f26da3a6e020eae4b9324946b74453ff7ad4cd4671798cae9b4d513af53b39958876a4188246c6094eb58a401b9dd383a40070f26bfa8b65a0fc2', 'TRCOM.rezende@gmail.com', '2014-09-08', '<p>Ol&aacute;, meu nome &eacute; TRCOM Rezende. Tenho 36 anos e sou formado em arquitetura e design pela FAUUSP (2004). Em 2005 fui um dos fundadores do Est&uacute;dio Camisa10, onde trabalhei por cinco anos. Entre 2010 e 2013 trabalhei como designer/programador na equipe de infografia online do portal VEJA.com. Atualmente moro em S&atilde;o Paulo, trabalho como freelancer e estou dispon&iacute;vel para contatos.</p>', '<p>en Nasci em dezembro de 1978, me formei arquiteto pela FAU USP em 2004 e desde ent&atilde;o atuo como designer multim&iacute;dia com enfoque em cria&ccedil;&atilde;o. Em 2005 fui um dos fundadores do Est&uacute;dio Camisa10 onde trabalhei durante quatro anos. <br /><br />Atualmente moro em S&atilde;o Paulo - Brasil, trabalho como freelancer e estou dispon&iacute;vel para contatos e contratos.</p>', '<p><strong>nome<br /></strong>TRCOM Pinheiro de Rezende</p>\r\n<p><strong>contato</strong><br /><a href="mailto:TRCOM.rezende@gmail.com" target="_blank">TRCOM.rezende@gmail.com</a></p>\r\n<p><strong>nascimento</strong><br />04/12/1978</p>\r\n<p><strong>forma&ccedil;&atilde;o</strong><br />Arquitetura e Urbanismo - <a href="http://www.usp.br/fau" target="_blank">FAU USP</a> (2004)</p>\r\n<p><strong>experi&ecirc;ncia profissional</strong><br />&bull;&nbsp;2005-2009: Est&uacute;dio Camisa10 - s&oacute;cio/fundador, gerenciamento de projetos, designer, programador e animador.<br />&bull;&nbsp;2010-2013: Veja.com - Infografista</p>\r\n<p><strong>idiomas</strong><br />ingl&ecirc;s - avan&ccedil;ado</p>\r\n<p><strong>palestras</strong><br />Design e Infografia -&nbsp;&nbsp;9&ordf; Semana do Design da ESPM-SP - Nov.2011</p>\r\n<p><strong>pr&ecirc;mios<br /></strong>Data Jornalism Awards 2012 - Ranking dos Estados Brasileiros (2&ordm;&nbsp;&nbsp;lugar)<br />INFOLIDE - Rede de Esc&acirc;ndalos (1&ordm;&nbsp;&nbsp;lugar)</p>\r\n<p><strong><strong>---&nbsp;</strong><br />habilidades</strong><br />Adobe After Effects<br />Adobe Photoshop<br />Adobe Flash<br />Adobe Illustrator<br />Adobe Dreamweaver<br />Adobe Premiere<br />Adobe InDesign<br />AutoCAD<br />Microsoft Office<br />Corel Draw<br />Sony SoundForge</p>\r\n<p><strong>linguagens</strong><br />PHP 5.0<br />ActionScript 2.0<br />ActionScript 3.0<br />Javascript<br />XML<br />CSS<br />HTML<br />MySQL</p>', '<p><b>name</b><br />TRCOM Pinheiro de Rezende</p>\n<p><b>contact</b><br /><a href="mailto:TRCOM.rezende@gmail.com" target="_blank">TRCOM.rezende@gmail.com</a><br />cel +5511 9616 2371&nbsp;</p>\n<p><b>birthdate</b><br />04/12/1978</p>\n<p><b>academic degree<br /></b>Architecture and Urbanism -&nbsp;<a href="http://www.usp.br/fau" target="_blank">FAU USP</a>&nbsp;(2004)</p>\n<p><b>professional experience</b><br />2005-2009: Camisa10 Studio - Art director, designer, programmer and motion designer</p>\n<p>2010-*: Veja.com - Infographic designer</p>\n<p><b>skills&nbsp;</b><br />Adobe After Effects<br />Adobe Photoshop<br />Adobe Flash<br />Adobe Illustrator<br />Adobe Dreamweaver<br />Adobe Premiere<br />Adobe InDesign<br />AutoCAD<br />Microsoft Office<br />Corel Draw<br />Sony SoundForge</p>\n<p><b>programming languages</b><br />PHP 5.0<br />ActionScript 2.0<br />ActionScript 3.0<br />Javascript<br />XML<br />CSS<br />HTML<br />MySQL</p>\n<p>&nbsp;</p>', '', 'Palestra Design e infografia - ESPM - Nov.2012. foto:João Lebrão', NULL, '<iframe src="//player.vimeo.com/video/41379347?title=0&byline=0&portrait=0&color=ffffff" width="720" height="405" frameborder="0" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>', '7252b5d388ba04fdc5db20540c47bb290158db30f973a0072489c8a95aaed2a2d31979da053bce6342a8af0ea90caffbf2c4a14ee591b7365a8ccdce9ae247452da56e83', '1366146045');
